import React from 'react';
import Navigation from "../../components/Nav/Navigation";

const Layout: React.FC = ({children}) => {
    return (
        <>
            <header>
                <Navigation/>
            </header>
            <main>
                {children}
            </main>
            <footer>
            </footer>
            <style jsx global>{`
                body {
                  margin: 0;
                  padding: 0;
                }
                #__next {                
                  min-height: 100vh;
                  display: flex;
                  flex-wrap: wrap;
                }
                main {
                    flex-grow: 1;
                    background-size: cover;
                    max-width: 1440px;
                    padding: 20px;
                    margin: 0 auto;
                }
            `}
            </style>
        </>
    )
};

export default Layout;
